import React, { useState } from 'react';
import './index.css';

const MobileScreen = ({ onJoin }) => {
  const [playerName, setPlayerName] = useState('');

  const handleJoin = () => {
    if (playerName) {
      onJoin(playerName);
      setPlayerName('');
    }
  };

  return (
    <div className="mobile-screen">
      
      <button onClick={handleJoin}>Join Game</button>
    </div>
  );
};

export default MobileScreen;
