import React, { useState } from 'react';
import QRCodeComponent from './QRCodeComponent';
import PlayerList from './components/PlayerList';
import Question from './Question';
import MobileScreen from './MobileScreen';
import MainScreen from "./components/MainScreen"

const App = () => {
  const [players, setPlayers] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const questions = [
    { question: 'What is 2 + 2?', options: ['A. 3', 'B. 4', 'C. 5', 'D. 6'], correct: 'B' },
    { question: 'Capital of France?', options: ['A. Berlin', 'B. Madrid', 'C. Paris', 'D. Rome'], correct: 'C' }
  ];

  const currentQuestion = questions[currentQuestionIndex];

  const handlePlayerJoin = (playerName) => {
    setPlayers([...players, playerName]);
  };

  const handleAnswer = (selectedAnswer) => {
    if (selectedAnswer === currentQuestion.correct) {
      alert('Correct!');
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      alert('Wrong answer!');
    }
  };

  if (currentQuestionIndex >= questions.length) {
    return <h1>Game Over! Thanks for playing.</h1>;
  }

  return (
    <div>
      <MainScreen players={players} />
      <MobileScreen onJoin={handlePlayerJoin} />
      <Question question={currentQuestion} onAnswer={handleAnswer} />
    </div>
  );
};

export default App;
